function ShoppingHome() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [currentTestimonial, setCurrentTestimonial] = useState(0);

  const { productList, productDetails } = useSelector(
    (state) => state.shopProducts
  );
  const { featureImageList } = useSelector((state) => state.commonFeature);
  const [openDetailsDialog, setOpenDetailsDialog] = useState(false);
  const { user } = useSelector((state) => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { toast } = useToast();

  function handleNavigateToListingPage(getCurrentItem, section) {
    sessionStorage.removeItem("filters");
    const currentFilter = { [section]: [getCurrentItem.id] };
    sessionStorage.setItem("filters", JSON.stringify(currentFilter));
    navigate(`/shop/listing`);
  }

  function handleGetProductDetails(getCurrentProductId) {
    dispatch(fetchProductDetails(getCurrentProductId));
  }

  function handleAddtoCart(getCurrentProductId) {
    dispatch(
      addToCart({
        userId: user?.id,
        productId: getCurrentProductId,
        quantity: 1,
      })
    ).then((data) => {
      if (data?.payload?.success) {
        dispatch(fetchCartItems(user?.id));
        toast({ title: "Product added to cart" });
      }
    });
  }

  useEffect(() => {
    if (productDetails !== null) setOpenDetailsDialog(true);
  }, [productDetails]);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % featureImageList.length);
    }, 2000);
    return () => clearInterval(timer);
  }, [featureImageList]);

  useEffect(() => {
    const testimonialTimer = setInterval(() => {
      setCurrentTestimonial((prev) => (prev + 1) % testimonials.length);
    }, 7000);
    return () => clearInterval(testimonialTimer);
  }, []);

  useEffect(() => {
    dispatch(
      fetchAllFilteredProducts({
        filterParams: {},
        sortParams: "price-lowtohigh",
      })
    );
  }, [dispatch]);

  useEffect(() => {
    dispatch(getFeatureImages());
  }, [dispatch]);

  return (
    <div className="flex flex-col min-h-screen font-[Inter] text-gray-900">
      {/* =================== Hero Section =================== */}
      <section className="flex flex-col lg:flex-row items-center justify-between bg-gray-50 py-20 px-6 lg:px-20">
        <div className="max-w-xl space-y-6">
          <h1 className="text-5xl font-bold leading-tight">
            Redefine Your Style with Every Step
          </h1>
          <p className="text-lg text-gray-600">
            Discover the latest fashion for Men, Women, and Kids — from timeless
            classics to trending designs that fit every mood.
          </p>
          <Button
            onClick={() => navigate("/shop/listing")}
            className="text-white bg-black hover:bg-gray-800"
          >
            Shop the Collection →
          </Button>

          <div className="bg-white shadow-md rounded-2xl p-6 mt-10 max-w-md">
            <div className="flex text-yellow-400 mb-2">
              {"★★★★★".split("").map((s, i) => (
                <span key={i}>{s}</span>
              ))}
            </div>
            <p className="italic text-gray-800 font-medium">
              “Trendy outfits, premium brands, and effortless style — all in one
              place!”
            </p>
            <p className="text-gray-600 mt-3 font-semibold">— Harper Jackson</p>
          </div>
        </div>

        <div className="mt-12 lg:mt-0">
          {featureImageList && featureImageList.length > 0 && (
            <img
              src={featureImageList[currentSlide]?.image}
              alt="Hero"
              className="w-[480px] h-[480px] object-cover rounded-3xl shadow-lg"
            />
          )}
        </div>
      </section>

      {/* =================== Shop by Category =================== */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-6">
          <h2 className="text-4xl font-bold text-center mb-4">
            Shop by Category
          </h2>
          <p className="text-center text-gray-600 mb-12">
            Explore outfits and essentials crafted for everyone — from smart
            formals to cozy casuals.
          </p>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-6">
            {categoriesWithIcon.map((category) => (
              <div
                key={category.id}
                onClick={() =>
                  handleNavigateToListingPage(category, "category")
                }
                className="relative cursor-pointer group rounded-2xl overflow-hidden shadow-md"
              >
                <img
                  src={category.image}
                  alt={category.label}
                  className="w-full h-60 object-cover transition-transform duration-500 group-hover:scale-110"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-black/30 to-transparent flex flex-col items-center justify-end pb-6">
                  <category.icon className="w-8 h-8 text-white mb-2" />
                  <p className="text-white font-semibold text-lg">
                    {category.label}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* =================== Shop by Brand =================== */}
      <section className="py-20 bg-gray-50">
        <div className="container mx-auto px-6">
          <h2 className="text-4xl font-bold text-center mb-4">Shop by Brand</h2>
          <p className="text-center text-gray-600 mb-12">
            From Nike to Zara — explore premium collections from your favorite
            global fashion brands.
          </p>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-6">
            {brandsWithIcon.map((brand) => (
              <div
                key={brand.id}
                onClick={() => handleNavigateToListingPage(brand, "brand")}
                className="relative cursor-pointer group rounded-2xl overflow-hidden shadow-md"
              >
                <img
                  src={brand.image}
                  alt={brand.label}
                  className="w-full h-48 object-cover transition-transform duration-500 group-hover:scale-110"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/30 to-transparent flex items-center justify-center">
                  <p className="text-white text-lg font-semibold tracking-wide">
                    {brand.label}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* =================== Featured Products =================== */}
     
      {/* =================== Top Rated Products =================== */}
      <TopRatedProducts  />
      <LatestProducts/>
      <FeaturedProducts/>


      {/* =================== Testimonials =================== */}
    <Testimonials/>
    <EcommerceFAQ/>

      <ProductDetailsDialog
        open={openDetailsDialog}
        setOpen={setOpenDetailsDialog}
        productDetails={productDetails}
      />

      <Footer />
    </div>
  );
}

export default ShoppingHome;





